INSERT INTO places VALUES(320,'bk_magazine','https://bk.asia-city.com/nightlife/article/bangkoks-best-rooftop-bars#Aesop’s','<article>Aesop’s</article>','2025-09-06 11:35:17.723562',13.732934100000000476,100.5605156000000111,NULL,NULL,NULL,'Aesop’s',NULL,'Relocating to their plush and extremely well-designed space in Thonglor, the plate-smashing Greek food specialists at Aesop’s took special brand of partying to the rooftop late in 2023. The newer location promises a view over Benjakitti park on the 25th floor rooftop, with interior design taking a cue from Athens. Pop around to check out the high ceilings, marble accents, Greek-style columns, and statues throughout.',NULL,NULL,NULL,NULL,'new',NULL,NULL,'2025-09-17 04:33:42',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL);
