INSERT INTO places VALUES(338,'bk_magazine','https://bk.asia-city.com/nightlife/article/bangkoks-best-rooftop-bars#Luz','<article>Luz</article>','2025-09-06 11:35:17.778966',13.707001399999998447,100.59998539999999156,NULL,NULL,NULL,'Luz',NULL,'On Nut isn’t the first place you think of when you hear “rooftop restaurant.” But that’s changing. Sitting on the  by Melia, Luz Tapas Bar is the brainchild of Chef de Cuisine Laia Ferrer, bringing her Barcelona heritage to life through Thai ingredients and Spanish flavors. The venue, which opened mid-September in 2023, offers two spacious areas: the Luz terrace and the Luz rooftop.',NULL,NULL,NULL,NULL,'new',NULL,NULL,'2025-09-17 04:33:42',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL);
