INSERT INTO places VALUES(532,'bk_magazine','https://bk.asia-city.com/restaurants/news/50-new-bangkok-restaurants-opened-2024-you-to-check-out#19','<article>Honobono Ramen</article>','2025-09-08 04:22:21.345406',13.678438600000000224,100.6274734000000004,NULL,NULL,NULL,'Honobono Ramen',NULL,'Udom Suk residents don’t have to waste time in Thonglor and Phrom Phong traffic for a bowl of their favorite noodles because Honobono ramen brings craft ramen right to their neighborhood. The shoyu ramen (B279), a chicken shoyu broth, is mixed with some fruit de la mer. This dish is finished off with sous vide chicken breast, chashu pork, pickled bamboo shoots, and ajitama (boiled eggs marinated in sauce). Try the shio ramen (B259) for that extra aromatic flavor straight from the sea.',NULL,NULL,NULL,NULL,'new',NULL,NULL,'2025-09-17 04:33:42',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL);
