INSERT INTO places VALUES(534,'bk_magazine','https://bk.asia-city.com/restaurants/news/50-new-bangkok-restaurants-opened-2024-you-to-check-out#21','<article>Illy Cafe</article>','2025-09-08 04:22:21.351069',13.744303799999999515,100.54064350000000427,NULL,NULL,NULL,'Illy Cafe',NULL,'Illy is already a household name with more than 160 cafes in major cities across the globe. Its Erawan branch marks the first venue in Thailand. Living up to its international reputation, the cafe provides more than your regular coffee order and serves up Italian classics from fusilli pesto to mortadella sandwiches. It also delivers on the dessert front with the multi layered coffee bean (B220), a combo of chocolate moose, coffee cake, and dark chocolate coating that’ll give the bite a crunchy-on-the-outside-fluffy-on-the-inside texture.',NULL,NULL,NULL,NULL,'new',NULL,NULL,'2025-09-17 04:33:42',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL);
