INSERT INTO places VALUES(556,'bk_magazine','https://bk.asia-city.com/restaurants/news/50-new-bangkok-restaurants-opened-2024-you-to-check-out#43','<article>Street Pizza</article>','2025-09-08 04:22:21.409029',13.732170499999998281,100.5667270000000002,NULL,NULL,NULL,'Street Pizza',NULL,'A little further down the bustling Thonglor streets and the fertile ground for cafes in Pridi, Pattanakarn is an untapped location for restaurants. As the name suggests, it’s all about the pizza at this spot. We recommend the classic margherita (B299) made from sourdough marinated up to 36 hours before using. The slices render a soft and fluffy feel which bodes well with their sweet tomato base and fresh basil.',NULL,NULL,NULL,NULL,'new',NULL,NULL,'2025-09-17 04:33:42',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL);
