INSERT INTO places VALUES(558,'bk_magazine','https://bk.asia-city.com/restaurants/news/50-new-bangkok-restaurants-opened-2024-you-to-check-out#45','<article>Take Eat Easy</article>','2025-09-08 04:22:21.414187',13.716291999999998818,100.54715800000000314,NULL,NULL,NULL,'Take Eat Easy',NULL,'Intending to run an all-day brunch eatery with a low carbon footprint, Et 1.5 gathers their produce in house. The eggs are taken from their farmed chickens, veggies are plucked from their backyard, and other ingredients are locally sourced to save the planet and your hunger pangs. For example, their organic Turkish Eggs from House Hen (B280) serves poached eggs alongside Loatian seaweed on top of homemade yogurt and cheese.',NULL,NULL,NULL,NULL,'new',NULL,NULL,'2025-09-17 04:33:42',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL);
INSERT INTO places VALUES(559,'bk_magazine','https://bk.asia-city.com/restaurants/news/50-new-bangkok-restaurants-opened-2024-you-to-check-out#46','<article>Tapori</article>','2025-09-08 04:22:21.416469',13.729937699999998912,100.57446720000000617,NULL,NULL,NULL,'Tapori',NULL,'Tapori, hindu slang for wanderer, takes diners beyond the cliche Indian cuisine, mining recipes from a more than 300 recipe database and narrowing it down to a top 29 first issue. We recommend the tawa-masala kaleji (B420). Traditionally found in Delhi weddings, the north meets south dish is a chicken liver flavored ice cream eaten on a bed of dosa crumble. Also order the milky ice cream benami kheer (B350) and take a stab at guessing the secret ingredient.',NULL,NULL,NULL,NULL,'new',NULL,NULL,'2025-09-17 04:33:42',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL);
