INSERT INTO places VALUES(561,'bk_magazine','https://bk.asia-city.com/restaurants/news/50-new-bangkok-restaurants-opened-2024-you-to-check-out#48','<article>Tokki</article>','2025-09-08 04:22:21.421315',13.730731999999998826,100.58606949999999713,NULL,NULL,NULL,'Tokki',NULL,'Tokki is a Korean restaurant and bar that’s not casual nor fine dining but a good in-between. The name translates to "rabbit," a term that appears throughout the restaurant. The space has plenty of indoor plants with a minimalist and neutral tone. The menu showsoff the playful side of Korean dining, like the Corn Kimchi Jeon, Korean pizza generously coated with cheese and corn; Snow Yukke, fried seaweed with marinated beef, topped with egg yolk and cheese; and their Bulgogi Fries featuring sweet marinated pork on top of thick fries. They also do Korean traditional liquor pairings which include honey coconut makgeolli with honeycomb and soju highballs.',NULL,NULL,NULL,NULL,'new',NULL,NULL,'2025-09-17 04:33:42',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL);
