PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE alembic_version (
	version_num VARCHAR(32) NOT NULL, 
	CONSTRAINT alembic_version_pkc PRIMARY KEY (version_num)
);
CREATE TABLE users (
	id INTEGER NOT NULL, 
	username VARCHAR(50) NOT NULL, 
	email VARCHAR(100) NOT NULL, 
	hashed_password VARCHAR(255) NOT NULL, 
	is_active BOOLEAN, 
	is_superuser BOOLEAN, 
	created_at DATETIME, 
	updated_at DATETIME, 
	PRIMARY KEY (id)
);
CREATE TABLE IF NOT EXISTS "places" (
	id INTEGER NOT NULL, 
	source TEXT, 
	source_url TEXT, 
	raw_payload TEXT, 
	scraped_at DATETIME, 
	lat FLOAT, 
	lng FLOAT, 
	address TEXT, 
	gmaps_place_id TEXT, 
	gmaps_url TEXT, 
	name TEXT, 
	category TEXT, 
	description_full TEXT, 
	summary TEXT, 
	tags_csv TEXT, 
	price_level INTEGER, 
	hours_json TEXT, 
	processing_status TEXT, 
	last_error TEXT, 
	published_at DATETIME, 
	updated_at DATETIME, picture_url TEXT, business_status TEXT, utc_offset_minutes INTEGER, summary_source TEXT, summary_version INTEGER, 
	PRIMARY KEY (id), 
	UNIQUE (source_url)
);
PRAGMA writable_schema=ON;
    name,
    tags_csv,
    summary,
    category,
    description_full,
    address,
    content=''places'',
    content_rowid=''id''
)');
CREATE TABLE IF NOT EXISTS 'fts_places_data'(id INTEGER PRIMARY KEY, block BLOB);
CREATE TABLE IF NOT EXISTS 'fts_places_idx'(segid, term, pgno, PRIMARY KEY(segid, term)) WITHOUT ROWID;
CREATE TABLE IF NOT EXISTS 'fts_places_docsize'(id INTEGER PRIMARY KEY, sz BLOB);
CREATE TABLE IF NOT EXISTS 'fts_places_config'(k PRIMARY KEY, v) WITHOUT ROWID;
CREATE UNIQUE INDEX ix_users_email ON users (email);
CREATE INDEX ix_users_id ON users (id);
CREATE UNIQUE INDEX ix_users_username ON users (username);
CREATE INDEX ix_places_id ON places (id);
CREATE TRIGGER fts_places_ai AFTER INSERT ON places BEGIN
            VALUES (new.id, new.name, new.tags_csv, new.summary, new.category, new.description_full, new.address);
        END;
CREATE TRIGGER fts_places_au AFTER UPDATE ON places BEGIN
            VALUES('delete', old.id, old.name, old.tags_csv, old.summary, old.category, old.description_full, old.address);
            VALUES (new.id, new.name, new.tags_csv, new.summary, new.category, new.description_full, new.address);
        END;
CREATE TRIGGER fts_places_ad AFTER DELETE ON places BEGIN
            VALUES('delete', old.id, old.name, old.tags_csv, old.summary, old.category, old.description_full, old.address);
        END;
CREATE TRIGGER places_ai AFTER INSERT ON places BEGIN
    VALUES (new.id, new.name, new.tags_csv, new.summary, new.category, new.description_full, new.address);
END;
CREATE TRIGGER places_au AFTER UPDATE ON places BEGIN
    UPDATE fts_places SET
        name = new.name,
        tags_csv = new.tags_csv,
        summary = new.summary,
        category = new.category,
        description_full = new.description_full,
        address = new.address
    WHERE rowid = new.id;
END;
CREATE TRIGGER places_ad AFTER DELETE ON places BEGIN
    DELETE FROM fts_places WHERE rowid = old.id;
END;
PRAGMA writable_schema=OFF;
COMMIT;
