INSERT INTO places VALUES(330,'bk_magazine','https://bk.asia-city.com/nightlife/article/bangkoks-best-rooftop-bars#Cru_Champagne','<article>Cru Champagne</article>','2025-09-06 11:35:17.755261',13.74781599999999937,100.53850099999999123,'Centara Grand at CentralWorld, 999/99 Rama 1 road,, Phatumwan, เขตปทุมวัน กรุงเทพมหานคร 10330, Thailand','ChIJG2ZLfjCZ4jAR-FXzxVf8_xQ','https://www.google.com/maps/place/?q=place_id:ChIJG2ZLfjCZ4jAR-FXzxVf8_xQ','Cru Champagne','Bar','The bar is located on what used to be a helicopter landing pad on top of the five-star hotel Centara Grand at CentralWorld. Access Cru from a little elevator at Red Sky, which drops you right on top of the hotel, where you get a stunning, 59th-floor, 360-degree view of Bangkok. The drinks list is all about Champagne, so come prepared to order a bottle of G.H. Mumm Cordon Rouge Brut.','Set atop a luxury hotel, this bar offers breathtaking 360-degree views of Bangkok from the 59th floor. Specializing in Champagne, guests can indulge in bottles like G.H. Mumm Cordon Rouge Brut while enjoying the chic ambiance. Access is via a discreet elevator, making it a unique spot for an upscale night out.','bar,champagne,views',NULL,'{"Mon": ["00:00-01:00", "17:00-24:00"], "Tue": ["00:00-01:00", "17:00-24:00"], "Wed": ["00:00-01:00", "17:00-24:00"], "Thu": ["00:00-01:00", "17:00-24:00"], "Fri": ["00:00-01:00", "17:00-24:00"], "Sat": ["00:00-01:00", "17:00-24:00"], "Sun": ["17:00-24:00", "00:00-01:00"]}','summarized',NULL,NULL,'2025-09-08 06:01:07.317376',NULL,'OPERATIONAL',420,'gpt',3);
