INSERT INTO places VALUES(412,'bk_magazine','https://bk.asia-city.com/health/article/best-spa-treatments-bangkok#Harnn_Spa','<article>Harnn Spa</article>','2025-09-06 11:44:51.192727',13.745172799999998414,100.53927360000001201,'10330, Thailand, Bangkok, Pathum Wan, Bangkok, Zen Tower @ CentralWorld 12Mth floor','ChIJbf5z0M-e4jAR-qM5CBJoDyI','https://www.google.com/maps/place/?q=place_id:ChIJbf5z0M-e4jAR-qM5CBJoDyI','Harnn Spa','Spa','Harnn Spa предлагает успокаивающую атмосферу с современными тайскими интерьерами и большими окнами. Здесь вы можете попробовать терапию Pranakorn: 60-минутный традиционный тайский массаж за B1,800, который использует движения, похожие на йогу, для восстановления подвижности и снятия мышечного напряжения. Также доступны ароматерапия, акупунктура и рефлексология. Адрес:  Central World, 4/5 Rajdamri Road, Pathumwan. Часы работы: ежедневно с 11:00 до 20:00.','A serene spa with modern Thai interiors offers a tranquil escape from the city''s hustle. Signature treatments include the Pranakorn therapy, featuring a 60-minute traditional Thai massage that enhances mobility and relieves tension. Aromatherapy, acupuncture, and reflexology services further enhance the wellness experience.','spa,relaxation,wellness',NULL,'{"Mon": ["10:00-21:00"], "Tue": ["10:00-21:00"], "Wed": ["10:00-21:00"], "Thu": ["10:00-21:00"], "Fri": ["10:00-21:00"], "Sat": ["10:00-21:00"], "Sun": ["10:00-21:00"]}','summarized',NULL,NULL,'2025-09-08 06:08:43.292796',NULL,'OPERATIONAL',420,'gpt',3);
