INSERT INTO places VALUES(414,'bk_magazine','https://bk.asia-city.com/health/article/best-spa-treatments-bangkok#Let’s_Relax_Spa','<article>Let’s Relax Spa</article>','2025-09-06 11:44:51.198660',13.736930199999999313,100.56012749999998501,'2, 2/1-4 Soi Phraek Samakkhi, Khwaeng Khlong Toei, Khet Khlong Toei, Krung Thep Maha Nakhon 10110, Thailand','ChIJbR0-0t2f4jARI8JTZ9MoIpI','https://www.google.com/maps/place/?q=place_id:ChIJbR0-0t2f4jARI8JTZ9MoIpI','Let’s Relax Spa','Spa','Let’s Relax Spa offers a unique interior and exterior design influenced by the local background, featuring Thai touches that create a calming atmosphere. Their services include a 120-minute Thai massage for B1,200 and a 90-minute aromatic hot stone treatment for B2,300. The spa is located on the 2nd floor of Park Lane Community Mall, Ekkamai Road, Watthana, and is open daily from 10am to midnight.','A calming atmosphere infused with Thai design welcomes visitors to this spa. Signature treatments include a soothing 120-minute Thai massage and a relaxing aromatic hot stone treatment. Open daily until midnight, it''s the perfect spot for unwinding after a long day.','spa,relaxation,wellness',NULL,'{"Mon": ["10:00-24:00"], "Tue": ["10:00-24:00"], "Wed": ["10:00-24:00"], "Thu": ["10:00-24:00"], "Fri": ["10:00-24:00"], "Sat": ["10:00-24:00"], "Sun": ["10:00-24:00"]}','summarized',NULL,NULL,'2025-09-08 06:08:52.613479',NULL,'OPERATIONAL',420,'gpt',3);
